var mongoose = require('mongoose'),
    Schema = mongoose.Schema;
var UserSchema = new mongoose.Schema(
    {
      username: String,
      password: String,
      email: String,
      firstName: String,
      lastName: String,
      isAdmin:Boolean
    },{collection: "userDetails"});

module.exports =mongoose.model('userDetails',UserSchema);
